import React, { Fragment, useState } from 'react';
import { connect } from 'react-redux';
import { Link, Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';
/* eslint-disable import/first */
// eslint-disable-next-line no-restricted-globals
// eslint-disable-next-line
import { setAlert } from '../../actions/alert';
//la fonction register de l'action auth

export const UserDetails = (setAlert, editFirstName, isUpdated) => {
  const [formData, setFormData] = useState({ firstname: '' });
  const { firstname } = formData;

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const onSubmit = async (e) => {
    //pour que la page ne se recharge pas
    e.preventDefault();
    editFirstName({ firstname });
  };

  if (isUpdated) {
    return <Redirect to='/dashboard' />;
  }

  return;

  <Fragment>
    <h1 className='text-info'>Registration</h1>

    <form className='form' onSubmit={(e) => onSubmit(e)}>
      <div className='form-group'>
        <input
          type='text'
          placeholder='FirstName'
          name='firstname'
          //   required
          onChange={(e) => onChange(e)}
        />
      </div>
      <input type='submit' className='btn btn-primary' value='Edit' />
    </form>
    <div className='listUsers'>
      {listUsers.length > 0 ? (
        listUsers.map((list) => (
          <div className='profile bg-light'>
            <img src={list.avatar} alt='' className='round-img' />
            <div>
              <h2>{list.lastname}</h2>

              <Link to={`/user/${list._id}`} className='btn btn-primary'>
                View Profile
              </Link>
            </div>
          </div>
        ))
      ) : (
        <h4>No users found...</h4>
      )}
    </div>
  </Fragment>;
};
//ptfr et entree
UserDetails.propTypes = {
  setAlert: PropTypes.func.isRequired,
  editFirstName: PropTypes.func.isRequired,
  isUpdated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
  isUpdated: state.auth.isAuthenticated,
});
export default connect(mapStateToProps, { setAlert, editFirstName })(
  UserDetails
);
