import React, { Fragment, useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Link, Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import Spinner from '../layout/Spinner';
import { getDetailUserById } from '../../actions/listUsers';

export const UserDetails = ({
  getDetailUserById,
  listUsers: { listUsers, loading },
  auth,
  match,
}) => {
  useEffect(() => {
    getDetailUserById(match.params.id);
  }, [getDetailUserById]);

  return <Fragment>details</Fragment>;
};
//ptfr et entree
UserDetails.propTypes = {
  listUsers: PropTypes.array.isRequired,
  getDetailUserById: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  listUsers: state.listUsers,
});
export default connect(mapStateToProps, { getDetailUserById })(UserDetails);
