import React, { Fragment, useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Link, Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import Spinner from '../layout/Spinner';
import getDetailUserById from '../../actions/listUsers';

export const UserDetails = ({
  getDetailUserById,
  listUsers: { listUsers, loading },
  auth,
  match,
}) => {
  useEffect(() => {
    getDetailUserById(match.params.id);
  }, [getDetailUserById]);

  const [formData, setFormData] = useState({ firstname: '' });
  const { firstname } = formData;

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const onSubmit = async (e) => {
    //pour que la page ne se recharge pas
    e.preventDefault();
    editFirstName({ firstname });
  };

  if (isUpdated) {
    return <Redirect to='/dashboard' />;
  }

  return (
    <Fragment>
      <h1 className='text-info'>View and Edit</h1>

      <form className='form' onSubmit={(e) => onSubmit(e)}>
        <div className='form-group'>
          <input
            type='text'
            placeholder='FirstName'
            name='firstname'
            //   required
            onChange={(e) => onChange(e)}
          />
        </div>
        <input type='submit' className='btn btn-primary' value='Edit' />
      </form>
      <div className='listUsers'>
        {listUsers.length > 0 ? (
          listUsers.map((list) => (
            <div className='profile bg-light'>
              <img src={list.avatar} alt='' className='round-img' />
              <div>
                <h2>{list.lastname}</h2>

                <Link to={`/user/${list._id}`} className='btn btn-primary'>
                  View Profile
                </Link>
              </div>
            </div>
          ))
        ) : (
          <h4>No users found...</h4>
        )}
      </div>
    </Fragment>
  );
};
//ptfr et entree
UserDetails.propTypes = {
  listUsers: PropTypes.array.isRequired,
  getDetailUserById: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  listUsers: state.listUsers,
});
export default connect(mapStateToProps, { getDetailUserById })(UserDetails);
