import React, { Fragment, useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Link, Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import Spinner from '../layout/Spinner';
import { getDetailUserById } from '../../actions/listUsers';

export const UserDetails = ({
  getDetailUserById,
  listUsers: { detailUser, loading, listUsers },
  auth,
  match,
}) => {
  useEffect(() => {
    getDetailUserById(match.params.id);
  }, [getDetailUserById]);

  return (
    <Fragment>
      {detailUser === null || loading ? (
        <Spinner />
      ) : (
        <Fragment>Profile</Fragment>
      )}

      {auth.isAuthenticated &&
        auth.loading === false &&
        auth.user === detailUser.user && (
          <Link to='/editUser' className='btn btn-dark'>
            Edit your Profile
          </Link>
        )}
    </Fragment>
  );
};
//ptfr et entree
UserDetails.propTypes = {
  detailUser: PropTypes.object.isRequired,
  getDetailUserById: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  detailUser: state.detailUser,
  listUsers: state.listUsers,
});
export default connect(mapStateToProps, { getDetailUserById })(UserDetails);
