import React, { useEffect, Fragment } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { getListUsers } from '../../actions/listUsers';
import { PropTypes } from 'prop-types';
import Spinner from '../layout/Spinner';

export const Dashboard = ({
  getListUsers,
  auth: { user },
  listUsers: { listUsers, loading },
}) => {
  useEffect(() => {
    getListUsers();
  }, []);
  return loading && listUsers === null ? (
    <Spinner />
  ) : (
    <Fragment>
      <h1 className='large text-primary'>Users</h1>
      <p className='lead'>
        <i className='fas fa-user'></i>
        welcomesss {user && user.firstname}
        <br />
        {listUsers.length > 0 ? (
          listUsers.map((list) => (
            <table className='table table-striped'>
              <thead>
                <tr key={list._id}>
                  <th scope='row'>
                    <img
                      id='target'
                      src={list.avatar}
                      alt=''
                      className='rounded-circle'
                      width='150'
                      height='150'
                    />
                  </th>
                  <td>
                    {' '}
                    {list.email} {list.lastname}
                  </td>

                  <td>
                    <Link to={`/user/${list._id}`} className='btn btn-primary'>
                      View Profile
                    </Link>
                  </td>
                </tr>
              </thead>
            </table>
          ))
        ) : (
          <span> No User found ...</span>
        )}
      </p>
    </Fragment>
  );
};

Dashboard.propTypes = {
  getListUsers: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  listUsers: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  listUsers: state.listUsers,
});
export default connect(mapStateToProps, { getListUsers })(Dashboard);
