import React, { useEffect, Fragment } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { getListUsers } from '../../actions/listUsers';
import { PropTypes } from 'prop-types';
import Spinner from '../layout/Spinner';

export const Dashboard = ({
  getListUsers,
  auth: { user },
  listUsers: { list, loading },
}) => {
  useEffect(() => {
    getListUsers();
  }, []);
  return loading && listUsers === null ? (
    <Spinner />
  ) : (
    <Fragment>
      <h1 className='text-info'>Users</h1>
      <p className='  font-size: 1.5rem,  margin-bottom: 1rem;'>
        <i className='fas fa-user'></i>
        welcome {user && user.firstname + ' ' + user.lastname}
        <br />
        <div className='listUsers'>
          {list.length > 0 ? (
            list.map((l) => (
              <div className='profile bg-light'>
                <img src={l.avatar} alt='' className='round-img' />
                <div>
                  <h2>{l.lastname}</h2>

                  <Link to={`/users/${l._id}`} className='btn btn-primary'>
                    View Profile
                  </Link>
                </div>
              </div>
            ))
          ) : (
            <h4>No users found...</h4>
          )}
        </div>
      </p>
    </Fragment>
  );
};

Dashboard.propTypes = {
  getListUsers: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  list: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  list: state.listUsers,
});
export default connect(mapStateToProps, { getListUsers })(Dashboard);
