import React, { Fragment, useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Link, Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import Spinner from '../layout/Spinner';
import { getDetailUserById } from '../../actions/listUsers';

export const UserDetails = ({
  getDetailUserById,
  listUsers: { detailUser, loading, listUsers },
  auth,
  match,
}) => {
  useEffect(() => {
    getDetailUserById(match.params.id);
  }, [getDetailUserById]);

  return (
    <Fragment>
      {detailUser === null || loading ? (
        <Spinner />
      ) : (
        <Fragment>
          <div className='profile bg-light'>
            <img src={detailUser.avatar} alt='' className='round-img' />
            <div>
              <h2>{detailUser.firstname}</h2>
              <p>
                {detailUser.status}{' '}
                {detailUser.lastname && <span> at {detailUser.lastname}</span>}
              </p>
              <p className='my-1'>
                {detailUser.lastname && <span>{detailUser.lastname}</span>}
              </p>
            </div>
            <ul>
              <li key={index} className='text-primary'>
                <i className='fas fa-check' /> {detailUser.lastname}
              </li>
            </ul>
          </div>

          {auth.isAuthenticated &&
            auth.loading === false &&
            auth.user._id === detailUser._id && (
              <Link to='/editUser' className='btn btn-dark'>
                Edit your Profile
              </Link>
            )}
        </Fragment>
      )}
    </Fragment>
  );
};
//ptfr et entree
UserDetails.propTypes = {
  detailUser: PropTypes.object.isRequired,
  getDetailUserById: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  detailUser: state.detailUser,
  listUsers: state.listUsers,
});
export default connect(mapStateToProps, { getDetailUserById })(UserDetails);
